import {
  ButtonBack, ButtonNext,
  CarouselProvider,
  Slide,
  Slider,
} from 'pure-react-carousel';
import React, { useContext } from 'react';
import RecipeContext from '../context/RecipeContext';

export default function Carrousel() {
  const { recomended } = useContext(RecipeContext);
  return (
    <div>
      <CarouselProvider
        naturalSlideWidth={ 80 }
        naturalSlideHeight={ 70 }
        totalSlides={ 6 }
        visibleSlides={ 2 }
        step={ 2 }
        orientation="horizontal"
        className="carousel"
        dragEnabled={ false }
      >
        <Slider>
          { recomended.map((rec, index) => (
            <Slide key={ index } index={ index }>
              <div data-testid={ `${index}-recommendation-card` } className="card">
                <img
                  src={ rec.strDrinkThumb || rec.strMealThumb }
                  alt={ rec.strDrink || rec.strMeal }
                />
                <h3 data-testid={ `${index}-recommendation-title` }>
                  { rec.strDrink || rec.strMeal }
                </h3>
              </div>
            </Slide>
          ))}
        </Slider>
        <ButtonBack>{ '<' }</ButtonBack>
        <ButtonNext>{ '>' }</ButtonNext>
      </CarouselProvider>
    </div>
  );
}
