import React, { useContext } from 'react';
import { useLocation } from 'react-router-dom/cjs/react-router-dom.min';
import Card from '../components/Card';
import Header from '../components/Header';
import RecipeContext from '../context/RecipeContext';
import './Recipes.css';

const doze = 12;

export default function Recipes() {
  const location = useLocation();
  const { meals, drinks } = useContext(RecipeContext);

  const listMeals = meals && meals.filter((_meal, index) => index < doze);
  const listDrinks = drinks && drinks.filter((_meal, index) => index < doze);
  return (
    <div>
      <Header />
      <h2>Recipes</h2>
      <div className="card-container">
        { location.pathname === '/meals' ? (
          listMeals && listMeals.map(({ strMeal, strMealThumb }, index) => (
            <Card
              key={ index }
              name={ strMeal }
              url={ strMealThumb }
              index={ index }
            />
          )))
          : (
            listDrinks && listDrinks
              .map(({ strDrink, strDrinkThumb }, index) => (
                <Card
                  key={ index }
                  name={ strDrink }
                  url={ strDrinkThumb }
                  index={ index }
                />
              ))
          )}
      </div>
    </div>
  );
}
