import React, { useContext, useState } from 'react';
import { useHistory, useLocation } from 'react-router-dom/cjs/react-router-dom.min';
import RecipeContext from '../context/RecipeContext';
import { fetchDrinks, fetchMeals } from '../services/fetchAPI';

export default function SearchBar() {
  const { setDrinks, setMeals } = useContext(RecipeContext);
  const location = useLocation();
  const [typeSearch, setTypeSearch] = useState('ingredient');
  const [search, setSearch] = useState('');
  const history = useHistory();

  const searchMeals = async () => {
    const mealsList = await fetchMeals(typeSearch, search);
    if (mealsList && mealsList.length === 1) {
      history.push(`/meals/${mealsList[0].idMeal}`);
    }
    if (mealsList === null) {
      global.alert('Sorry, we haven\'t found any recipes for these filters.');
    }
    setMeals(mealsList);
  };

  const searchDrinks = async () => {
    const drinksList = await fetchDrinks(typeSearch, search);
    if (drinksList && drinksList.length === 1) {
      history.push(`/drinks/${drinksList[0].idDrink}`);
    }
    if (drinksList === null) {
      global.alert('Sorry, we haven\'t found any recipes for these filters.');
    }
    setDrinks(drinksList);
  };

  const searchRecipe = () => {
    if (typeSearch === 'first-letter' && search.length > 1) {
      global.alert('Your search must have only 1 (one) character');
    }
    if (location.pathname === '/meals') {
      searchMeals();
    } else {
      searchDrinks();
    }
  };

  return (
    <div>
      <input
        type="text"
        data-testid="search-input"
        value={ search }
        onChange={ ({ target }) => setSearch(target.value) }
      />

      <label htmlFor="ingredient">
        <input
          id="ingredient"
          value="ingredient"
          data-testid="ingredient-search-radio"
          name="search"
          type="radio"
          onChange={ ({ target }) => setTypeSearch(target.value) }
        />
        Ingredient
      </label>

      <label htmlFor="name">
        <input
          id="name"
          value="name"
          data-testid="name-search-radio"
          name="search"
          type="radio"
          onChange={ ({ target }) => setTypeSearch(target.value) }
        />
        Name
      </label>

      <label htmlFor="first-letter">
        <input
          id="first-letter"
          value="first-letter"
          data-testid="first-letter-search-radio"
          name="search"
          type="radio"
          onChange={ ({ target }) => setTypeSearch(target.value) }
        />
        First letter
      </label>

      <button
        data-testid="exec-search-btn"
        onClick={ searchRecipe }
      >
        Search
      </button>
    </div>
  );
}
