import React, { useEffect, useState } from 'react';
import { useLocation, useParams } from 'react-router-dom/cjs/react-router-dom.min';
import { getFoodRecipesId, getDrinkRecipesId } from '../services/fetchAPI';

export default function RecipeDetails() {
  const [recipe, setRecipe] = useState({});

  const location = useLocation();
  const { id } = (useParams());

  useEffect(() => {
    const getFoodRecipes = async () => {
      const foodRecipes = await getFoodRecipesId(id);
      setRecipe(foodRecipes);
    };

    const getDrinkRecipes = async () => {
      const drinkRecipes = await getDrinkRecipesId(id);
      setRecipe(drinkRecipes);
    };

    if (location.pathname.includes('meals')) {
      getFoodRecipes();
    } else {
      getDrinkRecipes();
    }
  }, []);

  return (
    <div>
      <main>
        { location.pathname === `/meals/${id}` ? (
          <div>
            <img src={ recipe.strMealThumb } alt="imagem" />
          </div>
        ) : (
          <div>
            <img src={ recipe.strDrinkThumb } alt="imagem" />
          </div>
        ) }
        ;
      </main>
    </div>
  );
}
